/* eslint-disable no-undef */
import React from 'react';

import moment from 'moment';


const Post = ({ title, content, user, createdAt, stars, comments, onRemove, id, caption, aevent}) => {

  return (
    <article className="Post">
      <div className="Post--content">
        <h3>{title}</h3>
        <div>{content}</div>
        <br></br>
          <div style={{fontWeight: "lighter"}}>{caption}</div>
      </div>
      <div className="Post--meta">
        <div>
          <p>
            <span role="img" aria-label="star">
              TimeOnVideo:{" "}  
            </span>
            {aevent}min                                              
          </p>
          <p>{moment(createdAt).format()}</p>
        </div>
        <div>
          <button className="delete" onClick={() => onRemove(id)}>Delete</button>
        </div>
      </div>
    </article>
  );
};

Post.defaultProps = {
  title: 'An Incredibly Hot Take',
  content:
    'Lorem ipsum dolor sit, amet consectetur adipisicing elit. Ducimus est aut dolorem, dolor voluptatem assumenda possimus officia blanditiis iusto porro eaque non ab autem nihil! Alias repudiandae itaque quo provident.',
  user: {
    id: '123',
    displayName: 'Bill Murray',
    email: 'billmurray@mailinator.com',
    photoURL: 'https://www.fillmurray.com/300/300',
  },
  createdAt: new Date(),
  stars: 0,
  comments: 0,
};

export default Post;
