import React, { useState, useEffect } from "react";
import firebase from "firebase/app";
import 'firebase/storage';
import 'firebase/firestore';


     <script src="https://www.gstatic.com/firebasejs/8.1.1/firebase-app.js"></script>; 

    var firebaseConfig = {
    apiKey: "AIzaSyDtAj2ZHdxcrLPjOShy_XJ1WMYOnzzRPYc",
    authDomain: "annotation-222df.firebaseapp.com",
    databaseURL: "https://annotation-222df.firebaseio.com",
    projectId: "annotation-222df",
    storageBucket: "annotation-222df.appspot.com",
    messagingSenderId: "160008754115",
    appId: "1:160008754115:web:f99f37994fb6f76e02e376",
    measurementId: "G-ELJEDHCGF8",
    };
    firebase.initializeApp(firebaseConfig);
    console.log("hey"); 

const Notes = () => {
  const [title, updatetitle] = useState("");
  const [note, updatenote] = useState("");
  
  var ref = firebase.database.ref('notedata');
  function createNote() {
    if (title !== "" && note !== "")
      ref.push({
        title: title,
        note: note,
      });
  }
   return (
    <section className="noteform">
      <h3>Create New Note</h3>
      <div className="form-group">
        <label htmlFor="noteform-title">Title</label>
        <input
          type="text"
          id="noteform-title"
          name="noteform-title"
          value={title}
          placeholder="Title"
          onChange={(e) => updatetitle(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label htmlFor="noteform-note">Note</label>
        <textarea
          type="text"
          name="noteform-note"
          id="noteform-note"
          value={note}
          placeholder="Note"
          onChange={(b) => updatenote(b.target.value)}
        ></textarea>
      </div>
      <button onClick={this.showLeft.bind(this)}>Create Note</button>

      {/* <input
        id="location"
        value={location}
        placeholder="Location"
        onChange={(e) => updateLocation(e.target.value)}
      /> */}
    </section>
  );
};

export default Notes;
